            // Do X blur
            int i = 0;
            for (int y = 0; y &lt; H; y++) {
                for (int x = 0; x &lt; W; x++) {
                    int Y = 0, G = 0;
                    for (int k = 0; k &lt; s; k++) {
                        int c = source.get_color_for_convolution.getColor(x + k - h &lt;&lt; 8, y &lt;&lt; 8);
                        int w = d[k];
                        Y += w * (c &amp; 0xff00ff);
                        G += w * (c &amp; 0x00ff00);
                    }
                    temp[i++] = (0xff00ff00 &amp; Y | 0x00ff0000 &amp; G) &gt;&gt; 8;
                }
            }
            // Do Y blur
            i = 0;
            int notamount = 256 - amount;
            for (int y = 0; y &lt; h; y++) {
                for (int x = 0; x &lt; W; x++) {
                    int Y = 0, G = 0;
                    for (int k = 0; k &lt; s; k++) {
                        int y2 = (y - h + k + H) % H;
                        int c = temp[x + W * (y2)];
                        int w = d[k];
                        Y += w * (c &amp; 0xff00ff);
                        G += w * (c &amp; 0x00ff00);
                    }
                    int c1 = (0xff00ff00 &amp; Y | 0x00ff0000 &amp; G) &gt;&gt; 8;
                    int c2 = sourcebuffer.getElem(i) &lt;&lt; 1;
                    int r = ((c2 &gt;&gt; 16) &amp; 0x1fe) - ((c1 &gt;&gt; 16) &amp; 0xff);
                    int g = ((c2 &gt;&gt; 8) &amp; 0x1fe) - ((c1 &gt;&gt; 8) &amp; 0xff);
                    int b = ((c2) &amp; 0x1fe) - ((c1) &amp; 0xff);
                    r = r &lt; 0 ? 0 : r &gt; 0xff ? 0xff : r;
                    g = g &lt; 0 ? 0 : g &gt; 0xff ? 0xff : g;
                    b = b &lt; 0 ? 0 : b &gt; 0xff ? 0xff : b;
                    c2 = (r &lt;&lt; 16) | (g &lt;&lt; 8) | (b);
                    c2 = ColorUtility.average(c1, amount, c2, notamount);
                    destbuffer.setElem(i++, c2);
                }
            }
            for (int y = h; y &lt; Hp; y++) {
                for (int x = 0; x &lt; W; x++) {
                    int Y = 0, G = 0;
                    for (int k = 0; k &lt; s; k++) {
                        int c = temp[x + W * (y - h + k)];
                        int w = d[k];
                        Y += w * (c &amp; 0xff00ff);
                        G += w * (c &amp; 0x00ff00);
                    }
                    int c1 = (0xff00ff00 &amp; Y | 0x00ff0000 &amp; G) &gt;&gt; 8;
                    int c2 = sourcebuffer.getElem(i) &lt;&lt; 1;
                    int r = ((c2 &gt;&gt; 16) &amp; 0x1fe) - ((c1 &gt;&gt; 16) &amp; 0xff);
                    int g = ((c2 &gt;&gt; 8) &amp; 0x1fe) - ((c1 &gt;&gt; 8) &amp; 0xff);
                    int b = ((c2) &amp; 0x1fe) - ((c1) &amp; 0xff);
                    r = r &lt; 0 ? 0 : r &gt; 0xff ? 0xff : r;
                    g = g &lt; 0 ? 0 : g &gt; 0xff ? 0xff : g;
                    b = b &lt; 0 ? 0 : b &gt; 0xff ? 0xff : b;
                    c2 = (r &lt;&lt; 16) | (g &lt;&lt; 8) | (b);
                    c2 = ColorUtility.average(c1, amount, c2, notamount);
                    destbuffer.setElem(i++, c2);
                }
            }
            for (int y = Hp; y &lt; H; y++) {
                for (int x = 0; x &lt; W; x++) {
                    int Y = 0, G = 0;
                    for (int k = 0; k &lt; s; k++) {
                        int y2 = y - h + k;
                        if (y2 &lt; 0) {
                            y2 = 0;
                        else if (y2 &gt;= H) {
                            y2 = H - 1;
                        }
                        int c = temp[x + W * (y2)];
                        int w = d[k];
                        Y += w * (c &amp; 0xff00ff);
                        G += w * (c &amp; 0x00ff00);
                    }
                    int c1 = (0xff00ff00 &amp; Y | 0x00ff0000 &amp; G) &gt;&gt; 8;
                    int c2 = sourcebuffer.getElem(i) &lt;&lt; 1;
                    int r = ((c2 &gt;&gt; 16) &amp; 0x1fe) - ((c1 &gt;&gt; 16) &amp; 0xff);
                    int g = ((c2 &gt;&gt; 8) &amp; 0x1fe) - ((c1 &gt;&gt; 8) &amp; 0xff);
                    int b = ((c2) &amp; 0x1fe) - ((c1) &amp; 0xff);
                    r = r &lt; 0 ? 0 : r &gt; 0xff ? 0xff : r;
                    g = g &lt; 0 ? 0 : g &gt; 0xff ? 0xff : g;
                    b = b &lt; 0 ? 0 : b &gt; 0xff ? 0xff : b;
                    c2 = (r &lt;&lt; 16) | (g &lt;&lt; 8) | (b);
                    c2 = ColorUtility.average(c1, amount, c2, notamount);
                    destbuffer.setElem(i++, c2);
                }
            }

        } else {
            buffer.buffer.data_buffer = buffer.output.data_buffer;
        }
    }
